import React, { useState } from "react";
import NavBar from './navbar'
import Data from '../data'

function TechnologyVehicle ({data}) {

    return  <section className="flex flex-col justify-center items-center lg:flex-row lg:mx-auto lg:w-10/12 lg:items-start">
                <h1 className="my-3 sm:self-start sm:m-8 lg:mx-0"><span>03</span>space lauch 101</h1>
                <img className="my-4" src={data.images.landscape} alt="" />
                <div>
                    <button className="rounded-full bg-transparent border border-white p-4 text-white">1</button>
                    <button className="rounded-full bg-transparent border border-white p-4">2</button>
                    <button className="rounded-full bg-transparent border border-white p-4">3</button>
                </div>
            </section>
}


export default function Technology () {

    const [indexTech, setIndextech] = useState("0")
    let techData = Data.technology[indexTech]

    function handleClick (e) {
        setIndextech(e.target.id)
    }

    return  <div className="techno h-screen">
                <NavBar active="crew" />
                <TechnologyVehicle handleClick={handleClick} data={techData} indexCrew={indexTech} />
            </div>
}
